        return token && s.sys.token && token === s.sys.token;
    }

parseCliArgs.js:
(cliArgs) => {

        const args = {};

        for (let i = 0; i < cliArgs.length; i++) {
            if (i < 2) continue; //skip node scriptName args

            let arg = cliArgs[i];
            let [k, v] = arg.split('=');
            if (!v) {
                args[i - 2] = arg; //start write args from main 0
                continue;
            }
            k = k.slice(2); //remove "--" characters
            args[k.trim()] = v.trim();
        }

        return args;
    }

logger.js:
() => {
    return class Logger {
        constructor(prefix = '') {
            this.prefix = prefix;
            this.isMuted = 0;
        }
        mute() { this.isMuted = 1 } unmute() { this.isMuted = 0 }
        onMessage(handler) { this.handler = handler; return this; }

        async log() {
            let s = this.prefix + '';

            for (let i = 0; i < arguments.length; i++) {
                const m = arguments[i];
                const isObjOrArray = typeof m === 'object' && m !== null;

                if (m instanceof Error) {
                    s += m.stack ?? m.toString();
                } else {
                    s += isObjOrArray ? ' ' + JSON.stringify(m) : m;
                }
            }
            if (!this.isMuted) console.log(s);
            if (this.handler) this.handler(s);
        }
        async info() { await this.log(...arguments); }
        async error() { await this.log(...arguments); }
    }
}

cloneObject.js:
obj => {

    //ok
    if (Array.isArray(obj)) {
        let array = [];
        for (let i = 0; i < obj.length; i++) {

            const v = obj[i];
            const t = typeof v;

            if (t === 'function') continue;
            if (t === 'object' && v !== null) {
                array.push(cloneObject(v));
            } else {
                array.push(v);
            }
        }
        return array;
    }

    let clone = {};
    for (let k in obj) {
        const v = obj[k];
        const t = typeof v;
        if (t === 'function') continue;
        clone[k] = (t === 'object' && v !== null) ? cloneObject(v) : v;
    }
    return clone
}

