    }
}

dataBrowser.js:
async () => {
    return class dataBrowser {

        getV() { return this.v; }
        getTitle() { return 'Data browser'; }
        async init() {
            this.http = new (await s.f('sys.httpClient'));
            this.nodes = new Map;

            const v = await s.f('sys.ui.view');
            this.v = new v({ class: 'dataBrowser' });

            const DataNode = await s.f('sys.apps.GUI.dataNode');
            this.node = DataNode;
            const DataBrowserNode = await s.f('sys.apps.dataBrowser.node');
            this.outlinerNode = DataBrowserNode;

            const dataNode = new DataNode(s);
            const node = new DataBrowserNode;
            await node.init(dataNode, true, this);
            node.removeSubNodesShift();
            e('>', [node, this.v]);

            this.openedPaths = s.e('localState.get', 'openedPaths');
            if (this.openedPaths) {
                this.openedPaths = JSON.parse(this.openedPaths);
            } else {
                this.openedPaths = {};
            }

            //todo clear this.openedPaths which not exists in "s"
            this.addNode(node);
            await node.open(this.openedPaths);

            this.buffer = null;
        }
        addNode(dataBrowserNode) {
            this.nodes.set(dataBrowserNode.getId(), dataBrowserNode);
        }
        removeNode(id) {
            this.nodes.delete(id);
        }
        //deactivate() { this.v.hide(); }
        close() { }
        //isEmpty() { return this.outLinerRootNode.isEmpty()}
        getOutlinerNodeById(id) { return this.nodes.get(id); }

        async duplicate(outlinerNode) {

            const parentDataNode = outlinerNode.getParent().getDataNode();
            const dataNode = outlinerNode.getDataNode();
            const newK = dataNode.getKey() + '_copy';

            if (parentDataNode.get(newK)) {
                console.log(`Key ${newK} already exists in object.`); return;
            }
            let v = dataNode.getData();
            if (s.f('sys.isObject', v) || s.f('sys.isArray', v)) v = structuredClone(v);

            const newDataNode = new this.node(v);
            newDataNode.setKey(newK);
            const newOutlinerNode = new this.outlinerNode;
            await newOutlinerNode.init(newDataNode, false, this);

            e('>after', [newOutlinerNode.getV(), outlinerNode.getV()]);
            this.nodes.set(newOutlinerNode.getDomId(), newOutlinerNode);

            parentDataNode.set(newK, v);
            setTimeout(() => newOutlinerNode.focus(), 100);

            newDataNode.setPath(newOutlinerNode.getPath());
            s.e('state.update', { dataNode: newDataNode, data: v });
        }

        async handleKeydown(e) {

            return;

            if (!e.target.classList.contains('dataKey')) return;


            const outlinerNode = this.getOutlinerNodeById(e.target.getAttribute('outliner_node_id'));
            if (!outlinerNode) { console.log('outlinerNode not found'); return; }

            const k = e.key;
            const ctrl = e.ctrlKey || e.metaKey;

            if (k === 'Enter') {
                e.preventDefault();
            } else if (k === 'Tab') {
                e.preventDefault();
                //todo reimplement later
                // if (e.shiftKey) {
                //     const parent = outlinerNode.getParent();
                //     window.e('>after', [outlinerNode.getV(), parent.getV()]);
                // } else if (outlinerNode.prev()) {
                //     window.e('>', [outlinerNode.getV(), outlinerNode.prev().getNodesV()]);
                // }

            } /*else if (ctrl && k === 'ArrowUp' && outlinerNode.prev()) {
                window.e('>after', [outlinerNode.prev().getV(), outlinerNode.getV()]);
            } else if (ctrl && k === 'ArrowDown' && outlinerNode.next()) {
                window.e('>after', [outlinerNode.getV(), outlinerNode.next().getV()]);
            } */else {
                return;
            }
            e.target.focus();
            //await this.save();
        }

        async handleKeyup(e) {

            if (!e.target.classList.contains('dataKey')) return;

            const ignoreKeys = ['Enter', 'Tab', 'Control', 'Meta', 'ArrowLeft', 'ArrowRight', 'ArrowUp', 'ArrowDown'];
            if (new Set(ignoreKeys).has(e.key)) return;

            const outlinerNode = this.getOutlinerNodeById(e.target.getAttribute('outliner_node_id'));

            const dataNode = outlinerNode.getDataNode();
            const newK = e.target.innerText;
            const v = dataNode.getData();

            if (newK.length === 0) {
                if (!confirm('Delete object?')) return;
                s.e('state.del', { outlinerNode })
                return;
            }

            const oldPath = outlinerNode.getPath();
            const newPath = oldPath.length === 1 ? [newK] : [...oldPath.slice(0, -1), newK];

            if (newPath.toString() === oldPath.toString()) return;

            await this.http.post('/stateUpdate', { cmds: [{ newPath: newPath, oldPath: oldPath, op: 'mv' }] });
            const parentDataNode = outlinerNode.getParent().getDataNode();

            parentDataNode.set(newPath.at(-1), v);
            parentDataNode.del(oldPath.at(-1));

            dataNode.setKey(newK);
        }

        async handleClick(e) {

            const addOpenedNode = node => {
                //todo some func to direct iteration in depth of object by path
                let lastObj = this.openedPaths;
                const path = node.getPath();

                for (let i = 0; i < path.length; i++) {
                    const part = path[i];
                    if (!lastObj[part]) lastObj[part] = {};
                    lastObj = lastObj[part];
                }
                s.e('localState.set', ['openedPaths', JSON.stringify(this.openedPaths)]);
            }
            const deleteOpenedNode = node => {

                let lastObj = this.openedPaths;
                let lastPart;
                const path = node.getPath();

                for (let i = 0; i < path.length; i++) {
                    const part = path[i];
                    const isLastIndex = i === path.length - 1;
                    if (isLastIndex) {
                        delete lastObj[part];
                        break;
                    }
                    lastObj = lastObj[part];
                    lastPart = part;
                }
                s.e('localState.set', ['openedPaths', JSON.stringify(this.openedPaths)]);
            }
            const classList = e.target.classList;

            if (classList.contains('openClose') || classList.contains('openCloseArrow')) {

                let outlinerNode = this.getOutlinerNodeById(e.target.getAttribute('outliner_node_id'));
                if (!outlinerNode.hasSomethingToOpen()) {
                    return;
                }
                if (outlinerNode.isOpened) {
                    outlinerNode.close()
                    deleteOpenedNode(outlinerNode);
                } else {
                    outlinerNode.open();
                    addOpenedNode(outlinerNode);
                }
                return;
            }

            if (classList.contains('dataKey')) {

                let node = this.getOutlinerNodeById(e.target.getAttribute('outliner_node_id'));
                if (!node.hasSomethingToOpen()) return;
                if (!node.isOpened) {
                    node.open();
                    addOpenedNode(node);
                }
            }
        }
        handleContextmenu(e) {
            e.preventDefault();

            const isDataK = e.target.classList.contains('dataKey');
            const isDataV = e.target.classList.contains('dataValue');
            if (!isDataK && !isDataV) return;

            const node = this.getOutlinerNodeById(e.target.getAttribute('outliner_node_id'));
            const dataNode = node.getDataNode();
            const data = dataNode.getData();

            const v = s.f('sys.ui.view');
            const createBtn = (txt) => {
                return new v({ txt, class: ['btn', 'contextMenu', 'white', 'hoverGray'] });
            }

            const popup = s.sys.popup;
            let submenu;
            const removeSubmenu = () => {
                if (!submenu) return;
                submenu.clear();
                submenu = null;
            }

            let oBtn = createBtn('Open with');
            let openWithBtn = oBtn;
            oBtn.on('pointerenter', async () => {
                removeSubmenu();
                submenu = new (s.f('sys.apps.GUI.popup'));
                window.e('app.addViewElement', submenu);

                const apps = s.sys.apps;
                //todo const authorizedUserApps = s.users[user].apps;

                for (let name in apps) {
                    if (name === 'GUI') continue;
                    let appBtn = createBtn(name);
                    appBtn.on('click', () => {
                        window.e('openNode', { appPath: `sys.apps.${name}`, outlinerNode: node });
                        popup.clear();
                    });
                    window.e('>', [appBtn, submenu]);
                }
                submenu.putRightTo(openWithBtn);
            });
            window.e('>', [oBtn, popup]);


            if (typeof data === 'object' && data !== null) {
                oBtn = createBtn('Add item');
                oBtn.on('pointerenter', removeSubmenu);
                oBtn.on('click', () => {

                    if (Array.isArray(data)) {
                        data.push('item' + data.length + 1);
                    } else {
                        let c = 0;
                        while (1) {
                            c++;
                            const k = 'newKey' + c; const v = 'newValue';
                            if (data[k]) continue;

                            data[k] = v;
                            const dataNode = new this.node;
                            dataNode.setPath([...node.getPath(), k]);
                            s.e('state.update', { dataNode, data: v });
                            break;
                        }
                    }

                    node.reopen();
                    popup.clear();
                });
                window.e('>', [oBtn, popup]);
            }

            oBtn = createBtn('Copy');
            oBtn.on('click', () => {
                this.buffer = { mode: 'copy', node };
                popup.clear();
            });
            oBtn.on('pointerenter', removeSubmenu);
            window.e('>', [oBtn, popup]);

            oBtn = createBtn('Cut');
            oBtn.on('click', () => {
                this.buffer = { mode: 'cut', node };
                popup.clear();
            });
            oBtn.on('pointerenter', removeSubmenu);
            window.e('>', [oBtn, popup]);

            if (this.buffer) {
                oBtn = createBtn('Paste');
                oBtn.on('click', async () => {
                    if (!this.buffer) return;

                    const contextNodeData = node.getDataNode().getData();
                    if (!s.f('sys.isObject', contextNodeData) && !Array.isArray(contextNodeData)) return;

                    const bufOurlinerNode = this.buffer.outlinerNode;
                    const dataPath = bufOurlinerNode.getPath();

                    const dataNodeCopy = new this.node;
                    //todo in case of array we don't need to set key. just push to array
                    dataNodeCopy.setPath([...node.getPath(), dataPath.at(-1)]);

                    const data = structuredClone(bufOurlinerNode.getDataNode().getData());
                    s.e('state.update', { dataNode: dataNodeCopy, data });
                    if (this.buffer.mode === 'cut') {
                        await s.e('state.del', { outlinerNode: bufOurlinerNode });
                    }

                    this.buffer = null;
                    node.reopen(); //todo remember opened nodes
                    popup.clear();
                });
                oBtn.on('pointerenter', removeSubmenu);
                window.e('>', [oBtn, popup]);
            }

            oBtn = createBtn('Duplicate');
            oBtn.on('click', async () => {
                await this.duplicate(node);
                popup.clear();
            });
            oBtn.on('pointerenter', removeSubmenu);
            window.e('>', [oBtn, popup]);

            oBtn = createBtn('Console log');
            oBtn.on('pointerenter', removeSubmenu);
            oBtn.on('click', () => {
                s.l(dataNode);
                popup.clear();
            });
            window.e('>', [oBtn, popup]);

            oBtn = createBtn('Console path');
            oBtn.on('pointerenter', removeSubmenu);
            oBtn.on('click', () => {
                s.l(node.getPath());
                popup.clear();
            });
            window.e('>', [oBtn, popup]);

            oBtn = createBtn('Convert type to');
            let convertTypeBtn = oBtn;
            oBtn.on('pointerenter', () => {
                removeSubmenu();
                submenu = new (s.f('sys.apps.GUI.popup'));
                window.e('app.addViewElement', submenu);

                const types = ['Bool', 'String', 'Object', 'Array'];
                types.forEach(t => {
                    let btn = createBtn(t);
                    btn.on('click', () => {
                        let data;
                        if (t === 'Array') data = [];
                        else if (t === 'Bool') {
                            data = dataNode.getData() === 'false' ? false : true;
                        }
                        else if (t === 'Object') data = {};
                        else if (t === 'String') data = 'str';
                        else if (data === undefined) return;
                        s.e('state.update', { outlinerNode: node, data });

                        node.getParent().reopen();
                        popup.clear();
                    });
                    window.e('>', [btn, submenu]);
                });
                submenu.putRightTo(convertTypeBtn);

            });
            window.e('>', [oBtn, popup]);

            popup.onClear(() => removeSubmenu());
            popup.putRightToPointer({ x: e.clientX, y: e.clientY });
        }

    }
}

node.js:
async () => {

    return class dataBrowserNode {

        async init(dataNode, isRoot, dataBrowser) {
            this.dataNode = dataNode;
            this.dataBrowser = dataBrowser;
            this.domId = s.f('sys.uuid');

            const v = await s.f('sys.ui.view');
            this.v = new v({ id: this.domId, class: ['node'] });
            this.isOpened = false;
            this.isRoot = isRoot;

            if (!this.isRoot) await this.createDataFields(dataNode);

            this.subNodesV = new v({ class: ['subNodes', 'shift'] });
            e('>', [this.subNodesV, this.v]);
        }
        getId() { return this.domId; }

        //todo render undedined and null fields
        async createDataFields(dataNode) {
            const data = dataNode.getData();
            let dType = dataNode.getDataType();
            let dTypeLabel = { boolean: 'bool', function: 'f', number: 'num', object: '' }[dType] ?? dType;
            if (data === null) dTypeLabel = 'null';
            else if (data === undefined) dTypeLabel = 'undefined';

            const v = await s.f('sys.ui.view');
            const container = new v({ class: ['nodeContainer', 'flex'] }); e('>', [container, this.v]);

            this.openClose = new v({ class: 'openClose' });
            this.openClose.setAttr('outliner_node_id', this.domId);
            e('>', [this.openClose, container]);

            const openCloseArrow = new v({ class: 'openCloseArrow' });
            openCloseArrow.setAttr('outliner_node_id', this.domId);
            e('>', [openCloseArrow, this.openClose]);
            if (!this.hasSomethingToOpen()) this.openCloseBtnHide();

            this.keyV = new v({ class: 'dataKey', txt: dataNode.getKey() });
            this.keyV.setAttr('outliner_node_id', this.domId);
            this.keyV.toggleEdit();
            e('>', [this.keyV, container]);

            if (!s.f('sys.isObject', data) && !Array.isArray(data)) {
                e('>', [new v({ class: 'sep', txt: ':', style: { marginRight: '5px' } }), container]);
            }

            let slicedStrInfo;
            let opsBtn;
            const createValueV = (txt, className, color) => {
                if (txt === false) txt = 'false';

                return new v({ txt, class: className, style: { color: color } }); //whiteSpace: 'nowrap'
            }

            let valueV;

            if (dType === 'boolean') {
                valueV = createValueV(data, 'dataValue', 'blue');
                valueV.iEditMod();
            } else if (dType === 'number') {
                valueV = createValueV(data, 'dataValue', '#221199');
                valueV.iEditMod();
            } else if (dType === 'string') {

                const limit = 500;
                let isSliced = false;
                let str = data;
                if (str.length > limit) {
                    str = str.slice(0, limit).trim() + '...';
                    str = str.replaceAll('\n', '');
                    isSliced = true;
                }
                valueV = createValueV(str, ['dataValue', 'string'], '#AA1011');
                if (isSliced) {
                    slicedStrInfo = new v({ txt: 'and more ' + (data.length - limit) + ' chars', style: { marginLeft: '5px', 'white-space': 'nowrap' } });
                } else {
                    valueV.iEditMod();
                }

            } else {
                valueV = createValueV(dTypeLabel, 'dataType', 'gray')
            }
            valueV.setAttr('outliner_node_id', this.domId);
            valueV.on('keyup', async () => this.netUpdate());


            if (dType === 'string') e('>', [new v({ class: 'quote', txt: "'" }), container]);
            e('>', [valueV, container]);
            if (dType === 'string') e('>', [new v({ class: 'quote', txt: "'" }), container]);

            if (slicedStrInfo) e('>', [slicedStrInfo, container]);
            if (opsBtn) e('>', [opsBtn, container]);

            this.valueV = valueV;
        }

        async netUpdate() {
            const v = this.valueV.getTxt();
            if (!v.trim()) {
                if (confirm('Delete prop?')) {
                    s.e('state.del', { outlinerNode: this });
                }
                return;
            }
            if (v === this.getDataNode().getData()) return;

            s.e('state.update', { outlinerNode: this, data: v });
        }
        getDomId() { return this.domId }

        async requestData() {

            const path = this.getPath();
            const http = new (await s.f('sys.httpClient'));
            const { data } = await http.post('/state', { path });
            if (data) {
                this.dataNode.setData(data);

                //todo add method, getParentNode
                const parentNode = s.find(path.slice(0, -1));
                if (parentNode) {
                    parentNode[path[path.length - 1]] = data;
                }
            } else {
                //this.dataNode.getData()[s.sys.sym.IS_EMPTY] = true;
            }
        }

        async open(openedPaths = {}) {

            const dType = this.dataNode.getDataType();
            let v = this.dataNode.getData();
            if (dType !== 'object' || v === null) return;

            if (s.f('sys.isEmptyObject', v) || (Array.isArray(v) && v.length === 0)) {

                if (!v[s.sys.sym.IS_EMPTY]) await this.requestData();
            }

            v = this.dataNode.getData();

            const NodeClass = await s.f('sys.apps.GUI.dataNode');
            const OutlinerNodeClass = await s.f('sys.apps.dataBrowser.node');

            const renderNode = async (k, node) => {
                const dataNode = new NodeClass(node);
                dataNode.setKey(k);

                const outlinerNode = new OutlinerNodeClass;
                await outlinerNode.init(dataNode, false, this.dataBrowser);
                e('>', [outlinerNode.getV(), this.getNodesV()]);
                this.dataBrowser.addNode(outlinerNode);

                if (openedPaths[k] && outlinerNode.hasSomethingToOpen()) {
                    await outlinerNode.open(openedPaths[k]);
                }
            }

            if (Array.isArray(v)) {
                for (let i = 0; i < v.length; i++) await renderNode(i, v[i]);
            } else {
                const nameNode = {};
                for (let k in v) {
                    const kLower = k.toLowerCase();
                    if (!nameNode[kLower]) nameNode[kLower] = [];
                    nameNode[kLower].push({ k, node: v[k] });
                }
                const sortedKeys = Object.keys(nameNode).sort();

                for (let i = 0; i < sortedKeys.length; i++) {

                    const nodes = nameNode[sortedKeys[i]];
                    for (let j = 0; j < nodes.length; j++) {
                        await renderNode(nodes[j].k, nodes[j].node);
                    }
                }
            }
            this.isOpened = true;
            if (this.openClose) this.openClose.addClass('opened');
        }
        async close() {
            const nodesV = this.getNodesV();
            const children = nodesV.getChildren();
            //todo should be recursive
            for (let i = 0; i < children.length; i++) {
                const id = children[i].id;
                if (!id) throw Error('no outliner_node_id on ' + children[i]);
                this.dataBrowser.removeNode(id);
            }
            nodesV.clear();
            this.isOpened = false;
            if (!this.isRoot) {
                this.openClose.removeClass('opened');
            }
        }

        reopen() {
            //todo close all items recursive and open again
            if (this.isOpened) this.close();
            this.open();
        }

        hasSomethingToOpen() {
            const data = this.getDataNode().getData();
            const dType = this.getDataNode().getDataType();

            if (dType !== 'object' || data === null) return false;

            return true;
            //if (Array.isArray(data)) return data.length > 0;
            //return !s.f('sys.isEmptyObject', data);
        }

        async showOutlinerOpsPopup(opsBtn) {
            s.popup.clear();

            const v = await s.f('sys.ui.view');

            const createBtn = txt => new v({ txt, class: 'btn' });

            let oBtn = createBtn('Add');
            oBtn.on('click', async () => {
                this.getDataNode().set('newProp', 'value');
                this.openClose.visibilityShow();
                await this.close();
                await this.open();
                s.popup.clear();
            });
            e('>', [oBtn, s.popup]);

            // oBtn = createBtn('delete'); oBtn.on('click', e => {
            //     if (confirm('Delete prop?')) {
            //         s.e('state.del', {outlinerNode: this});
            //     }
            //     s.popup.clear();
            // });
            // e('>', [oBtn, s.popup]);

            // oBtn = createBtn('console.log'); oBtn.on('click', e => {
            //     console.log(this.getDataNode());
            //     s.popup.clear();
            // });
            e('>', [oBtn, s.popup]);

            s.popup.putRightTo(opsBtn);
        }
        openCloseBtnShow() { this.openClose.visibilityShow(); }
        openCloseBtnHide() { this.openClose.visibilityHide(); }

        //isEmpty() { return !this.dataNodesV.getDOM().children.length }
        isInRoot() { return this.getParent().isRoot }
