async () => {
    return class List {

        list = [];

        add(item, index = null) {

            if (index !== null) {
                if (typeof index !== 'number') throw Error(`Index type is [${typeof index}].`);
                this.list.splice(index, 0, item);

                return index;
            }

            this.list.push(item);

            return this.getLength() - 1;
        }
        del(index) { this.list.splice(index, 1) };
        delLast() { this.list.pop(); };
        pop() { return this.list.pop() }
        get(index) { return this.list[index]; }
        getAll() { return this.list; }
        getFirst() { return this.list[0]; }
        getLast() { return this.list[this.getLastIndex()] }
        getLastIndex() { return this.list.length - 1; }
        getLength() { return this.list.length; }
        isEmpty() { return this.getLength() === 0; }
        iterate(f) {
            for (let i = 0; i < this.list.length; i++) f(this.list[i], i);
        }
        reset() { this.list = []; }
    }

}

os.js:
async () => {

    const {spawn, exec} = (await import('node:child_process'));

    return class OS {
        constructor(logger) {
            this.logger = logger;
            this.proc = null;
        }
        async run(cmd, detached, shell, procCallback, closeCallback) {

            let args = cmd.split(' ');
            let firstArg = args.shift();

            const p = spawn(firstArg, args, {shell, detached});
            this.proc = p;
            if (procCallback) await procCallback(p);

            p.stdout.on('data', data => this.logger.info(data.toString().trim()));
            p.stderr.on('data', data => this.logger.error(data.toString().trim()));
            p.on('error', err => this.logger.error(err.toString()));
            p.on('close', code => {
                this.logger.info({m: 'Process close:', code})
                if (closeCallback) closeCallback(code);
            });
        }

        async ex(cmd) {
            return new Promise((resolve, reject) => {
                exec(cmd, (err, stdout, stderr) => resolve({err, stdout, stderr}));
            });
        }
    }
}

