    const deliverDir = async (ssh, name) => {
        const zipName = name + '.zip';
        await os.ex(`zip -vr ${zipName} ${name} -x "*.DS_Store"`);

        const r = await ssh.execCommand('unzip');
        if (r.stderr.includes('unzip: command not found')) {
