        }
        await ssh.putFile(zipName, `./${zipName}`);
        await ssh.execCommand(`unzip -o ./${zipName}`);
    }

    const checkForNecessaryFiles = async (node) => {
        let ls = (await node.ssh.execCommand('ls')).stdout.split('\n');
